    setCart([...cart, { ...book, quantity: 1 }])
  }

  function removeFromCart(book) {
    console.log('remove from cart')
    // setCart([...cart, { ...book, quantity: 0}])
  }

  function changeQuantity(book, quantity) {
    setCart(
      cart.map((item) =>
        item.id === book.id
          ? {
            ...item,
            quantity: +quantity,
          }
          : item
      )
    )
